	byte[] convertFrame(byte[] oldFrame){
		byte[] newFrame = new byte[1000];
		if(oldFrame != null){
			for(int i=0; i<6; i++){
				newFrame[i] = oldFrame[i];
			}
			newFrame[6] = 2;//version
			newFrame[7] = 0;//id_1
			newFrame[8] = (byte) 1;//id_2
			for(int i=0; i<991; i++){
				newFrame[i+9] = oldFrame[i+6];
			}
		}
		return newFrame;
	}
	
	private void crc(byte[] array){
		CRC32 crc32 = new CRC32();
		crc32.update(array);
		long crcLong = crc32.getValue();
		short crcShort = (short) (crcLong&0xffff);
		array[8] = (byte)(crcShort>>>8);
		array[9] = (byte)(crcShort&0xff);
	}
	
